import React from 'react'
import * as d3 from 'd3'

import * as graphUtils from '../../lib/graph_utils.js'

import nodesData from '../../data/graphs/heavenly/nodes.json'
import edgesData from '../../data/graphs/heavenly/edges.json'

import styles from '../../styles/sketches/ski_graph.module.scss'

const SVG_WIDTH = 500,
      SVG_HEIGHT = 500

const colorMap = {
  "blue": "#1E88E5",
  "green": "#229954"
}

const delay = 500
const fadeInText  = () => d3.select("#ski-graph .text").transition().duration(delay).style("opacity", 1)
const fadeOutText = () => d3.select("#ski-graph .text").transition().duration(delay).style("opacity", 0)

// moving these functions out of graphUtils for now.
function makeAdjacencyList(edges, nodes) {
  let adjacencyList = new Map()
  for (const {start, end} of edges) {
    if (adjacencyList.has(start)) {
      adjacencyList.get(start).push(end)
    } else {
      adjacencyList.set(start, [end])
    }
  }
  for (let i = 0; i < nodes.length; i++) {
    if (!adjacencyList.has(i)) adjacencyList.set(i, [])
  }
  return adjacencyList
}

// takes start and end nodes and returns [path_exists, path]
function bfs(start, end, edges, nodes) {
  if (start === end) return [true, [start]]

  const adjacencyList = makeAdjacencyList(edges, nodes)

  let q = [[start]]
  let seen = new Set([start])

  while (!(q.length === 0)) {
    const q_length = q.length
    for (let i = 0; i < q_length; i++) {
      const path = q[i]
      const curr_node = path[path.length - 1]
      for (const neighbor of adjacencyList.get(curr_node)) {
        if (neighbor === end) {
          return [true, path.concat(neighbor)]
        }
        if (!seen.has(neighbor)) {
          seen.add(neighbor)
          q.push(path.concat(neighbor))
        }
      }
    }
    q = q.slice(q_length)
  }
  return [false, []]
}

function highlightPath(path, onAnimationFinish = () => {}, transitionLength = 500, delayUnit = 100) {
  d3.selectAll(`g.${styles.node}, g.edge`).transition().duration(300).style("opacity", 0.3)

  d3.selectAll(`g.${styles.node}`)
    .each((_, i, nodes) => {
      if (path.includes(i)) {
        d3.select(nodes[i])
          .transition()
          .duration(transitionLength)
          .delay(path.indexOf(i) * 2 * delayUnit + 3 * delayUnit)
            .style("opacity", 1)
      }
    })

  d3.selectAll("g.edge")
    .each((_, i, nodes) => {
      d3.select(nodes[i])
        .transition()
        .duration(transitionLength)
        .delay((d) => {
          for (let j = 0; j < path.length - 1; j++) {
            if (path[j] === d.start && path[j + 1] === d.end) {
              return (j * 2 + 1)* delayUnit + 3 * delayUnit
            }
          }
        })
          .style("opacity", 1)
    })

  const animationLength = (path.length * 2 + 3) * delayUnit
  setTimeout(onAnimationFinish, animationLength)
}

class SkiGraph extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      start: null,
      end: null,
    }
  }

  render() {
    const reset = () => {
      this.setState({start: null, end: null})
      d3.selectAll(`g.${styles.node} circle`).classed(styles.selected, false)
      d3.selectAll(`g.${styles.node}`).transition().duration(500).style("opacity", 1)
      d3.selectAll("g.edge").transition().duration(500).style("opacity", 1)
    }

    let displayText
    let onTextClick
    let textClass = styles.text
    if (this.state.start === null) {
      displayText = "Pick start"
    } else if (this.state.end === null) {
      displayText = "Pick end"
    } else {
      // on this graph there should be a path between every pair of nodes
      const [_, path] = bfs(this.state.start, this.state.end, edgesData, nodesData)
      highlightPath(path, () => setTimeout(fadeInText, 500))
      displayText = "Reset"
      onTextClick = reset
      textClass += " " + styles.reset_button
    }

    return (
      <div id="ski-graph" className={styles.ski_graph}>
        <p
          onClick={onTextClick}
          className={textClass}
        >{displayText}</p>

        <svg width={SVG_WIDTH} height={SVG_HEIGHT}></svg>
      </div>
    )
  }

  componentDidMount() {
    let svg = d3.select("#ski-graph svg")
    graphUtils.setUpSvg(svg)

    const scaledNodes = graphUtils.scaleNodeData(nodesData, SVG_WIDTH, SVG_HEIGHT)

    const onNodeClick = (e, d) => {
      if (this.state.start != null && this.state.end != null) return

      const i = scaledNodes.indexOf(d)

      // this is a pretty clumsy way to do animations but it's simple enough in this case.
      if (this.state.start == null) {
        fadeOutText()
        setTimeout(
          () => {
            this.setState({start: i})
            fadeInText()
          },
          delay
        )
      } else if (this.state.end == null) {
        fadeOutText()
        setTimeout(
          // fading the text back in is handled in a callback after the animation is finished
          () => this.setState({end: i}),
          delay
        )
      }
      d3.select(e.target).classed(styles.selected, true)
    }

    const nodeOpts = {
      onClick: onNodeClick,
      className: styles.node,
    }

    const edgeOpts = {
      strokeFn: (d) => d.level ? colorMap[d.level] : "grey"
    }

    graphUtils.renderEdgesD3({svg: svg, edgeData: edgesData, nodeData: scaledNodes, directed: true, opts: edgeOpts})
    graphUtils.renderNodesD3({svg: svg, nodeData: scaledNodes, opts: nodeOpts})
  }
}

// Downstream code is set up to display only functional components.
export default function SkiGraphFn() { return <SkiGraph /> }

import MIDISounds from 'midi-sounds-react';
import React, { useState } from 'react'
import { CSSTransition } from "react-transition-group"
import { Clear, PlayArrow, Stop } from '@material-ui/icons';

import data from '../../data/chords/chord_to_successors.json'

const TONIC = 50
const TEMPO = 120

const CHORD_SHAPES = {
  "maj":  [0, 4, 7],
  "maj7": [0, 4, 7, 10],
  "min":  [0, 3, 7],
  "sus":  [0, 5, 7],
  "dim":  [0, 3, 6, 9],
  "aug":  [0, 4, 8],
}
let CHORDS = {}
for (const [name, notes] of Object.entries(CHORD_SHAPES)) {
  CHORDS[name] = notes.map(note => note + TONIC)
}
const OFFSETS = {
  "b7": -2,
  "7":  -1,
  "1":  0,
  "b2": 1,
  "2":  2,
  "b3": 3,
  "3":  4,
  "4":  5,
  "5":  7,
  "b6": 8,
  "6":  9,
}

const TRANSITION_TIME = 300 // ms

const ChordList = ({chords, onSelect, inProp}) => {
  const [height, setHeight] = useState(0)
  return (
    <CSSTransition
      in={inProp}
      appear={true}
      timeout={1000}
      onEntering={() => setHeight(40 * chords.length)}
      onExit={() => setHeight(0)}
    >
      <div className="chords" style={{height: height}}>
        {/* chords *should* be unique */}
        {chords.map((chord, i) => {
          const [startOpacity, endOpacity] = [1, 0.3]
          let opacity
          if (chords.length === 2) {
            // hack - don't want a gradient for picking the first chord
            opacity = 1
          } else {
            opacity = startOpacity + (i / chords.length) * (endOpacity - startOpacity)
          }

          return (
            <p
              className="chord"
              chord={chord}
              onClick={onSelect}
              style={{opacity: opacity}}
              key={chord}
            >{chord}</p>)
        })}
        <style jsx>{`
          .chords {
            display: flex;
            flex-direction: column;
            align-items: center;
            transition: height ${TRANSITION_TIME}ms;
            overflow: hidden;
          }

          .chord {
            cursor: pointer;
            margin: 0.5em;
          }
        `}</style>
      </div>
    </CSSTransition>
  )
}

const NextChordPicker = ({data, onSelect, inProp}) => {
  let chords = []
  for (const chord in data) {
    chords.push([chord, data[chord]])
  }
  chords.sort((a, b) => b[1] - a[1])
  chords = chords.map((x) => x[0])
  return (
    <div className="next-chord-picker">
      <ChordList chords={chords} onSelect={onSelect} inProp={inProp} />
    </div>
  )
}

const FirstChordPicker = ({onSelect, inProp}) => (
  <div className="first-chord-picker">
    <ChordList chords={["1maj", "1min", "2min", "4maj", "5maj"]} onSelect={onSelect} inProp={inProp} />
  </div>
)

const ProgressionDisplay = ({chords, active, onSelect, listInProp}) => {
  chords = [...chords]
  const active_index = chords.length
  const pad = 4 - chords.length
  for (let i = 0; i < pad; i++) { chords.push("?") }

  return (
    <div className="progression-display">
      {chords.map((chord, i) => {
        return (
          <div key={i}>
            <p className={"progression-chord " + (chord !== "?" ? "selected" : "")}>{chord}</p>
            {active_index === 0 && i === 0 && active &&
              <FirstChordPicker onSelect={onSelect} inProp={listInProp} />}
            {active_index === i && i > 0 && active &&
              <NextChordPicker data={data[chords[i - 1]]} onSelect={onSelect} inProp={listInProp} />}
          </div>
        )
      })}
      <style jsx>{`
        .progression-display {
          display: flex;
          opacity: 1;
        }

        .progression-chord {
          margin: 1em;
          padding: 0.5em;
          border: 1px solid black;
          border-radius: 0.5em;
          width: 3em;
          text-align: center;
          transition: opacity ${TRANSITION_TIME}ms;
        }

        .progression-chord:not(.selected) {
          opacity: 0.3;
        }
      `}</style>
    </div>
  )
}

const Text = ({clickable, text, onClick, inProp}) => {
  return (
    <CSSTransition
      in={inProp}
      appear={true}
      timeout={TRANSITION_TIME}
    >
      <div
        className={"text " + (clickable ? "clickable" : "")}
        onClick={onClick}
      >
        <p>{text}</p>
        <style jsx>{`
          .text {
            font-weight: 400;
            height: 2em;
            margin: 0.5em;
            transition: opacity ${TRANSITION_TIME}ms;
          }

          .clickable {
            cursor: pointer;
          }

          .enter {
            opacity: 0;
          }

          .enter-active, .enter-done {
            opacity: 1;
          }

          .exit-active, .exit-done {
            opacity: 0;
          }

          p {
            font-size: 1.5em;
            margin: 0;
          }
        `}</style>
      </div>
    </CSSTransition>
  )
}

class ChordPicker extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      chords: [],
      started: false,
      textInProp: true,
      listInProp: false
    }
  }

  render() {
    const resetChords = () => {
      this.midiSounds.stopPlayLoop()
      this.setState({chords: [], textInProp: true})
    }

    const chordToNotes = (chord) => {
      let offsetLen
      if (chord.startsWith("b")) {
        offsetLen = 2
      } else {
        offsetLen = 1
      }
      const offset = chord.substring(0, offsetLen)
      const shape = chord.substring(offsetLen, chord.length)
      return CHORDS[shape].map(x => x + OFFSETS[offset])
    }

    const playChords = () => {
      if (this.state.chords.length == 0) return
      const beat = this.state.chords.map((chord) => [
        [], // drums
        [
          [3, chordToNotes(chord), 1]
        ]
      ])
      this.midiSounds.startPlayLoop(beat, TEMPO, 1)
    }

    const selectChord = (e) => {
      const chord = e.target.getAttribute("chord")
      this.setState({listInProp: false})
      if (this.state.chords.length === 3) {
        this.setState({textInProp: false})
      }

      setTimeout(
        () => {
          this.setState(
            (oldState) => { return {chords: oldState.chords.concat([chord]), listInProp: true} },
            () => {
              if (this.midiSounds.loopStarted) {
                this.midiSounds.stopPlayLoop()
                playChords()
              }
            }
          )
        },
        TRANSITION_TIME
      )

    }

    const clear_enabled = this.state.chords.length > 0
    const play_enabled = this.state.chords.length === 4
    const stop_enabled = this.state.chords.length === 4

    let text
    if (!this.state.started) {
      text = "Click to start!"
    } else if (this.state.chords.length === 0) {
      text = "Pick your first chord"
    } else if (this.state.chords.length < 4) {
      text = "Pick your next chord"
    } else {
      text = ""
    }
    let textOnClick
    if (this.state.started) {
      textOnClick = undefined
    } else {
      textOnClick = () => {
        this.setState({textInProp: false})
        setTimeout(
          () => this.setState({started: true, textInProp: true, listInProp: true}),
          TRANSITION_TIME
        )
      }
    }

    const iconSize = "30px"

    return (
      <div id="chord-picker">
        <MIDISounds
          ref={(ref) => (this.midiSounds = ref)}
          appElementName="__next"
          instruments={[3]}
        />

        <Text
          text={text}
          onClick={textOnClick}
          clickable={!this.state.started}
          inProp={this.state.textInProp}
        />

        <div className="controls">
          <span
            className={"button " + (clear_enabled ? "enabled" : "disabled")}
            onClick={clear_enabled ? resetChords : undefined}
           ><Clear fontSize="inherit" /></span>
          <span
            className={"button material-icons-outlined " + (play_enabled ? "enabled" : "disabled")}
            onClick={play_enabled ? playChords : undefined}
          ><PlayArrow fontSize="inherit" /></span>
          <span
            className={"button material-icons-outlined " + (stop_enabled ? "enabled" : "disabled")}
            onClick={stop_enabled ? () => this.midiSounds.stopPlayLoop() : undefined}
          ><Stop fontSize="inherit" /></span>
        </div>

        <ProgressionDisplay
          chords={this.state.chords}
          active={this.state.started}
          onSelect={selectChord}
          listInProp={this.state.listInProp}
        />
      <style jsx>{`
        #chord-picker {
          display: flex;
          flex-direction: column;
          align-items: center;
          margin-top: 5em;
          min-height: 750px;
        }

        .button {
          font-size: 30px;
          margin: 4px;
          cursor: pointer;
          transition: opacity ${TRANSITION_TIME}ms;
        }

        .button.enabled {
          opacity: 1;
        }

        .button.disabled {
          opacity: 0.3;
        }

        // doesn't work
        .MIDISounds {
          display: none;
        }
      `}</style>
      </div>
    )
  }
}

export default function ChordPickerFn() { return <ChordPicker /> }

import React from 'react'
import * as THREE from 'three'
import * as d3 from 'd3'

import styles from '../../styles/sketches/pipes.module.scss'

class Pipes extends React.Component {
  render() {
    return (
      <div id="pipes-container" className={styles.container}>
        <button name="start">Start/Reset</button>
        <div style={{display: "flex", alignItems: "center"}}>
          <p>Rotate Camera:</p>
          <input type="checkbox" name="toggle-rotate"/>
        </div>
        <div className={styles.canvas + " canvas"}></div>
      </div>
    )
  }

  componentDidMount() {
    let container = d3.select("#pipes-container .canvas").node(),
        bbox = container.getBoundingClientRect();

    let scene, camera;

    let renderer = new THREE.WebGLRenderer();
    renderer.setPixelRatio(window.devicePixelRatio);

    renderer.setSize(bbox.width, bbox.height);
    container.appendChild(renderer.domElement);

    const SPEED = 3, // units/frame
          R = 1, // cylinder radius
          L = 80, // size of cube boundary
          RADIAL_SEGMENTS = 32,
          MAX_SEGMENTS = 70, // max segments per pipe
          MIN_LENGTH = 5, // min length of a pipe segment
          MAX_LENGTH = 20,
          CUBE_CENTER = [L/2, L/2, L/2];

    const initialPositions = [
      [L/2, 0, L/2],
      [3/4*L, 3/4*L, 0],
      [L/2, L, L/2],
    ];
    const colors = [
      0x3182bd, // blue
      0x229954, // green
      0xF08080, // coral
    ];
    const MAX_PIPES = colors.length;
    let materials = [];
    for (const color of colors) {
      materials.push(new THREE.MeshStandardMaterial({color: color}));
    }

    const bounds = new THREE.Box3(new THREE.Vector3(0, 0, 0), new THREE.Vector3(L, L, L));

    // utils
    const randInt = function(min, max) {
      return Math.floor(Math.random() * (max - min)) + min;
    }

    const randChoice = function(arr) {
      return arr[Math.floor(Math.random()*arr.length)];
    }

    const newDirection = function(old_dir, position) {
      // return a new direction vector such that:
      //   - new direction is orthogonal to old direction
      //   - a pipe going in new_direction with MAX_LENGTH will stay in bounds
      let validDirs = directions.filter((new_dir) => {
        return (vector_dot(old_dir, new_dir) === 0) &&
              willBeInBounds(new_dir, position)
      });
      return randChoice(validDirs);
    }

    const willBeInBounds = function(dir, position) {
      const end_position = vector_add(position, vector_scale(dir, MAX_LENGTH + 2));
      return bounds.containsPoint(new THREE.Vector3(...end_position));
    }

    // utility functions for working with vectors
    // could probably make this cleaner by not converting between
    // js lists and THREE.Vector3's
    const pos_to_vec = function(pos) {
      return [pos.x, pos.y, pos.z];
    }

    const vector_scale = function(v, s) {
      let result = [];
      for (let i = 0; i < v.length; i++) {
        result.push(v[i] * s);
      }
      return result;
    }

    const vector_add = function(v1, v2) {
      let result = [];
      for (let i = 0; i < v1.length; i++) {
        result.push(v1[i] + v2[i]);
      }
      return result;
    }

    const vector_dot = function(v1, v2) {
      let result = 0;
      for (let i = 0; i < v1.length; i++) {
        result += v1[i] * v2[i];
      }
      return result;
    }

    const createCylinder = function() {
      let geometry = new THREE.CylinderGeometry(R, R, 0, RADIAL_SEGMENTS);
      let material = materials[numPipes];
      let mesh = new THREE.Mesh(geometry, material);
      scene.add(mesh);
      return mesh;
    }

    const createSphere = function() {
      let geometry = new THREE.SphereGeometry(R * 1.2, RADIAL_SEGMENTS, RADIAL_SEGMENTS);
      let material = new THREE.MeshStandardMaterial({color: colors[numPipes]});
      let mesh = new THREE.Mesh(geometry, material);
      scene.add(mesh);
      return mesh;
    }

    const rotateCylinder = function(c) {
      if (Math.abs(c.direction[0]) === 1) {
        c.mesh.rotateZ(Math.PI / 2);
      } else if (Math.abs(c.direction[2]) === 1) {
        c.mesh.rotateX(Math.PI / 2);
      }
    }

    const computeCameraPos = function() {
      const x = L * 1.5 * Math.sin(theta);
      const y = L * 1.5 * (Math.cos(theta) + 1);
      return [x, L/2, y];
    }

    const updateCamera = function() {
      camera.position.set(...computeCameraPos())
      camera.lookAt(...CUBE_CENTER)
    }

    const directions = [
      [1, 0, 0],
      [0, 1, 0],
      [0, 0, 1],
      [-1, 0, 0],
      [0, -1, 0],
      [0, 0, -1],
    ]

    let numSegments; // number of pipe segments that have been created
    let numPipes; // number of pipes that have been created
    let c; // keeps track of the current cylinder segment
    let theta = 8; // current camera rotation
    let omega = 0.01; // rotational speed
    let rotateCamera = false;

    let animationRequestId;

    const animate = function() {
      animationRequestId = requestAnimationFrame(animate);

      if (c.length < c.max_length) {
        c.length += SPEED;
        c.mesh.geometry.dispose();
        c.mesh.geometry = new THREE.CylinderGeometry(R, R, c.length, RADIAL_SEGMENTS);
        // need to make sure cylinder only grows in one direction
        const old_pos = pos_to_vec(c.mesh.position);
        const new_pos = vector_add(old_pos, vector_scale(c.direction, SPEED/2));
        c.mesh.position.set(...new_pos);
      } else if (numSegments++ < MAX_SEGMENTS) {
        // new pipe segment
        const end_position = vector_add(pos_to_vec(c.mesh.position), vector_scale(c.direction, c.length/2));
        const old_pos = pos_to_vec(c.mesh.position);
        const new_pos = vector_add(old_pos, vector_scale(c.direction, c.length/2));
        c = {
          length: 0,
          direction: newDirection(c.direction, end_position),
          max_length: randInt(MIN_LENGTH, MAX_LENGTH),
          mesh: createCylinder(),
        }
        c.mesh.position.set(...new_pos);
        rotateCylinder(c);
        // create sphere at junction
        let sphere = createSphere();
        sphere.position.set(...new_pos);
      } else if (numPipes < MAX_PIPES) {
        // finish the old pipe with a sphere
        const position = vector_add(pos_to_vec(c.mesh.position), vector_scale(c.direction, c.length/2));
        let sphere = createSphere();
        sphere.position.set(...position);

        if (numPipes < MAX_PIPES - 1) {
          numPipes++;
          numSegments = 0;
          newPipe(initialPositions[numPipes]);
        } else {
          init();
        }
      }

      if (rotateCamera) {
        theta += omega;
        updateCamera();
      }

      renderer.render(scene, camera);
    }

    const newPipe = function(position) {
      c = {
        length: 0,
        direction: newDirection([0, 0, 0], position),
        max_length: randInt(3, 5),
        mesh: createCylinder(),
      };
      rotateCylinder(c);
      c.mesh.position.set(...position);

      let s = createSphere();
      s.position.set(...position);
    }

    const setScene = function() {
      scene = new THREE.Scene();
      camera = new THREE.PerspectiveCamera(75, bbox.width/bbox.height, 0.1, 1000);

      updateCamera();

      let light = new THREE.AmbientLight(0x404040, 3);
      scene.add(light);

      let pointLight = new THREE.PointLight(0x404040, 3, 0);
      pointLight.position.set(L, L, L);
      scene.add(pointLight);

      renderer.render(scene, camera);
    }

    const init = function() {
      setScene();

      numSegments = 0;
      numPipes = 0;

      newPipe(initialPositions[0]);

      cancelAnimationFrame(animationRequestId);

      animate();
    }

    setScene();

    d3.select("#pipes-container button[name=start]").on("click", init);
    d3.select("#pipes-container input[name=toggle-rotate").on("change", () => rotateCamera = !rotateCamera)
  }
}

// Downstream code is set up to display only functional components.
export default function PipesFn() { return <Pipes /> }
